if($('.indicator').length > 0){
	const indicator = document.querySelector(".indicator");
	const input = document.querySelector("input");
	const weak = document.querySelector(".weak");
	const medium = document.querySelector(".medium");
	const strong = document.querySelector(".strong");
	const text = document.querySelector(".text");
	//const showBtn = document.querySelector(".showBtn");
	let regExpWeak = /[a-z]/;
	let regExpMedium = /\d+/;
	let regExpStrong = /.[!,@,#,$,%,^,&,*,?,_,~,-,(,)]/;
	function chkPassword(e){
		if(e.value != ""){
			indicator.style.display = "block";
			indicator.style.display = "flex";
			if(e.value.length <= 8 && (e.value.match(regExpWeak) || e.value.match(regExpMedium) || e.value.match(regExpStrong)))no=1;
			if(e.value.length >= 8 && ((e.value.match(regExpWeak) && e.value.match(regExpMedium)) || (e.value.match(regExpMedium) && e.value.match(regExpStrong)) || (e.value.match(regExpWeak) && e.value.match(regExpStrong))))no=2;
			if(e.value.length >= 10 && e.value.match(regExpWeak) && e.value.match(regExpMedium) && e.value.match(regExpStrong))no=3;
			if(no==1){
				weak.classList.add("active");
				text.style.display = "block";
				text.style.color = "red";
				text.textContent = "Your password is too week";
				text.classList.add("weak");
			}else{
				weak.classList.remove("active");
				text.classList.remove("weak");
				text.style.color = "";
			}
			if(no==2){
				medium.classList.add("active");
				text.style.color = "blue";
				text.textContent = "Your password is medium";
				text.classList.add("medium");
			}else{
				medium.classList.remove("active");
				text.classList.remove("medium");
				text.style.color = "";
			}
			if(no==3){
				weak.classList.add("active");
				medium.classList.add("active");
				strong.classList.add("active");
				text.style.color = "green";
				text.textContent = "Your password is strong";
				text.classList.add("strong");
			}else{
				strong.classList.remove("active");
				text.classList.remove("strong");
				text.style.color = "";
			}
			//showBtn.style.display = "block";
		}else{
			indicator.style.display = "none";
			text.style.display = "none";
			//showBtn.style.display = "none";
		}
	}
}

