	//Signup and forgotPwd Ajax Call
	$('.signupForm, .forgotPwd').on('submit', function (e) {
		if (e.isDefaultPrevented()) {
            // handle the invalid form...
        } else {
			e.preventDefault();
		
			var myform = $(this);
			var formdata = $(myform).serialize();
			var ajaxUrl = $(myform).attr('action');
			$.ajax({
				type: 'POST',
				url: ajaxUrl,
				data: formdata,
				success: function(data) {
					var result = $.parseJSON(data);
					if(result.hasOwnProperty('nameStatus') && result.nameStatus == 0){
						$('.error-name').text(result.nameMsg);
					}else{
						$('.error-name').text('');
					}
					if(result.hasOwnProperty('companyNameStatus') && result.companyNameStatus == 0){
						$('.error-company-name').text(result.companyNameMsg);
					}else{
						$('.error-company-name').text('');
					}
					if(result['apiStatus'] == '1'){
						swal({
							title: "Hurray!",
							text: result['apiMessages'],
							icon: "success",
							showCancelButton: false,
							closeOnConfirm: true,
							showLoaderOnConfirm: true
						})
						.then(() => {
						  if(result.hasOwnProperty('url') && result.url.length > 0){
							window.location.href = result.url;
						  }else{
							location.reload();
						  }
						});
					}else{
						swal({
							title: "Opps!",
							text: result['apiMessages'],
							icon: "warning",
							showCancelButton: false,
							closeOnConfirm: true,
							showLoaderOnConfirm: true
						})
						.then(() => {
						  if(result.hasOwnProperty('url') && result.url.length > 0){
							window.location.href = result.url;
						  }else{
							location.reload();
						  }
						});
					}
				},
				error: function() {
					swal("Opps!", 'There is some error in Signup!!!', "warning");
				}
			});
		}
	});
	
